mapper.select:
select code, id, title, use_cash, use_point, total_price, pay_method, to_char(pay_date,'yyyy-mm-dd') pay_date from (select rownum rnum, code, id, title, use_cash, use_point, total_price, pay_method, pay_date from (select p.code, p.id, b.title, p.use_cash, p.use_point, b.sale_price-(p.use_cash+p.use_point) total_price, p.pay_method, p.pay_date from book b, purchase p where (b.book_code=p.book_code)
where ${field} like '%'||#{ keyword }||'%'
order by pay_date desc)) where rnum between #{startNum} and #{endNum}

select code, id, title, use_cash, use_point, total_price, pay_method, to_char(pay_date,'yyyy-mm-dd') pay_date, to_char(rental_start,'yyyy-mm-dd') rental_start, to_char(rental_end,'yyyy-mm-dd') rental_end from (select rownum rnum, code, id, title, use_cash, use_point, total_price, pay_method, pay_date, rental_start, rental_end from (select r.code, r.id, b.title, r.use_cash, r.use_point, b.rental_price-(r.use_cash+r.use_point) total_price, r.pay_method, r.pay_date, r.rental_start, r.rental_end from book b, rental r where (b.book_code=r.book_code)
where ${field} like '%'||#{ keyword }||'%'
order by pay_date desc)) where rnum between #{startNum} and #{endNum}
